	/**
	 * 根据套装效果名称得到 套装号
	 * @param skillName
	 * @return
	 */
	public static Integer gettaozhuangNumber(String effectName){
		
		if(StringUtil.isEmpty(effectName)){
			return null;
		}
		String taozhuangStr = "";
		
		
		if(effectName.indexOf("追加法术") != -1){
			taozhuangStr = String.valueOf(suitAppendSkillsMap.get(effectName.replace("追加法术", "")));
		}else if(effectName.indexOf("变身术之") != -1){
			taozhuangStr = String.valueOf(suitTransformSkillsMap.get(effectName.replace("变身术之", "")));
		}else if(effectName.indexOf("变化咒之") != -1){
			taozhuangStr = String.valueOf(suitTransformCharmsMap.get(effectName.replace("变化咒之", "")));
		}else if(effectName.indexOf("附加状态") != -1){
			taozhuangStr = String.valueOf(suitAddedStatusMap.get(effectName.replace("附加状态", "")));
		}
		
		return Integer.valueOf(taozhuangStr.replace(".0", ""));
	}
	
	
